import React, { Component } from "react";
import ReactDOM from "react-dom";
import ReactCardCarousel from "react-card-carousel";
import { Link } from "react-router-dom";
import deepq from "./db/deepq.js"
import mainLayout from "./mainLayout.js";
import "./Play.css"
import TinderCard from './react-tinder-card/index'

const db = [
  {
    name: 'Richard Hendricks',
    url: './img/richard.jpg'
  },
  {
    name: 'Erlich Bachman',
    url: './img/erlich.jpg'
  },
  {
    name: 'Monica Hall',
    url: './img/monica.jpg'
  },
  {
    name: 'Jared Dunn',
    url: './img/jared.jpg'
  },
  {
    name: 'Dinesh Chugtai',
    url: './img/dinesh.jpg'
  }
]

class Play extends Component {
  static get CONTAINER_STYLE() {
    return {
      position: "relative",
      height: "100vh",
      width: "100%",
      display: "flex",
      flex: 0.3,
      justifyContent: "center",
      borderColor: '#FFFF00',
      alignItems: "middle"
    };
  }

  static get CARD_STYLE() {
    return {
      height: "420px",
      width: "350px",
      paddingTop: "80px",
      paddingBottom: "80px",
      paddingLeft: "80px",
      paddingRight: "80px",
      textAlign: "center",
      color: "black",
      background: "white",
      fontFamily: "avenir-medium",
      fontSize: "25px",
      borderRadius: "10px",
      borderColor: 'black',
      borderTopStyle:'solid',
      borderRightStyle:'solid',
      borderLeftStyle:'solid',
      borderBottomStyle:'solid',
      boxSizing: "border-box"
    };
  }


  state = {
    dataQuestion:[],
    lastDirection:useState(),
    setLastDirection : useState()
  };


  setStatefunction = () => {
    //  use reactjs setState
    let shuffled = deepq.sort( () => Math.random() - 0.5)
    this.setState({
      dataQuestion: shuffled
    });
  };

  characters = [
    {
      name: 'Richard Hendricks',
      url: './img/richard.jpg'
    },
    {
      name: 'Erlich Bachman',
      url: './img/erlich.jpg'
    },
    {
      name: 'Monica Hall',
      url: './img/monica.jpg'
    },
    {
      name: 'Jared Dunn',
      url: './img/jared.jpg'
    },
    {
      name: 'Dinesh Chugtai',
      url: './img/dinesh.jpg'
    }
  ];


  swiped = (direction, nameToDelete) => {
    console.log('removing: ' + nameToDelete)
    setLastDirection(direction)
  }

  outOfFrame = (name) => {
    console.log(name + ' left the screen!')
  }

  componentDidMount() {
    this.setStatefunction();
  }

  render() {
    return (
      <div>
      <div className = "previewdesc">
        <h2 className = 'previewtitle'>  take it slow. </h2>
        <h3> (disclaimer: emotions at your own risk) </h3>
      </div>
      <div style={Play.CONTAINER_STYLE}>
      <ReactCardCarousel autoplay={false} autoplay_speed={2500}>
      {this.state.dataQuestion.map(data => {
        return(
          <div style={Play.CARD_STYLE}>
          {data.question}
          <p id = 'name'> honestly. </p>
          </div>
        );
      })}
      </ReactCardCarousel>
      </div>
      <h1>React Tinder Card</h1>
      <div className='cardContainer'>
        {this.characters.map((character) =>
          <TinderCard className='swipe' key={character.name} onSwipe={(dir) => this.swiped(dir, character.name)} onCardLeftScreen={() => this.outOfFrame(character.name)}>
            <div style={{ backgroundImage: 'url(' + character.url + ')' }} className='card'>
              <h3>{character.name}</h3>
            </div>
          </TinderCard>
        )}
      </div>
      </div>
    );
  }
}

export default mainLayout(Play);
