import React, { Component } from "react";
import ReactDOM from "react-dom";
import ReactCardCarousel from "react-card-carousel";
import { Link } from "react-router-dom";
import basicq from "./db/basicq.js"
import { slide as Menu } from 'react-burger-menu'
import { Card, Button, CardTitle, CardText,CardBody } from 'reactstrap';

class Play extends Component {
  state = {
    dataQuestion:[]
  };

  static get CONTAINER_STYLE() {
    return {
      position: "relative",
      height: "100vh",
      width: "100%",
      display: "flex",
      flex: 0.3,
      justifyContent: "center",
      borderColor: '#FFFF00',
      alignItems: "middle"
    };
  }

  static get CARD_STYLE() {
    return {
      height: "420px",
      width: "350px",
      paddingTop: "100px",
      paddingBottom: "80px",
      paddingLeft: "100px",
      paddingRight: "100px",
      textAlign: "center",
      background: "#FFFFFF",
      color: "#000000",
      fontFamily: "avenir-black",
      fontSize: "30px",
      borderRadius: "10px",
      boxSizing: "border-box",
      shadowColor: '#FFF300',
      shadowOpacity: 100,
      shadowRadius: 12,
    };
  }

  setStatefunction = () => {
    //  use reactjs setState
    this.setState({
      dataQuestion: basicq
    });
  };


  componentDidMount() {
    this.setStatefunction();
  }

  render() {
    return (
      <div>
      <Link to="/" className="link">
      <h1 className='title' style = {{'textDecoration': 'none'}}> honestly. </h1>
      </Link>
      <ReactCardCarousel autoplay={false} autoplay_speed={2500}>

      {this.state.dataQuestion.map(data => {
        return(
                <Card body outline color="danger">
                <CardBody>
                 <CardTitle>{data.question}</CardTitle>
                 </CardBody>
                </Card>
        );
      })}

      </ReactCardCarousel>

      <div style={Play.CONTAINER_STYLE}>

      <div style={Play.CARD_STYLE}>{data.question}</div>
        </div>
      </div>
    );
  }
}

export default Play;
